import React, { useState } from 'react';

const MandateAccessButton = ({ endpoints }) => {
  const [showDetails, setShowDetails] = useState(false);
  const [detailsDictionary, setDetailsDictionary] = useState({});

  const handleButtonClick = async () => {
    try {
      const fetchRequests = endpoints.map(async endpoint => {
        const response = await fetch(`http://localhost:3010/${endpoint}`);
        if (!response.ok) {
          throw new Error(`Failed to fetch data from ${endpoint}`);
        }
        const data = await response.json();
        console.log(`Fetched data from ${endpoint}:`, data);

        // Example handling for single object response
        const details = Array.isArray(data) ? data.map(item => ({
          traderMandateId: item.traderMandateId,
          traderName: item.traderName
        })) : [{
          traderMandateId: data.traderMandateId,
          traderName: data.traderName
        }];

        return { [endpoint]: details };
      });

      const results = await Promise.all(fetchRequests);
      const newDetailsDictionary = results.reduce((acc, result) => {
        const key = Object.keys(result)[0]; // endpoint name
        acc[key] = result[key];
        return acc;
      }, {});

      setDetailsDictionary(newDetailsDictionary);
      setShowDetails(true);
    } catch (error) {
      console.error('Error fetching data:', error);
      // Handle error state or notification here if needed
    }
  };

  return (
    <div>
      <button onClick={handleButtonClick}>Generate Mandate Access</button>
      {showDetails && (
        <div>
          <h2>Trader Mandate Details</h2>
          {Object.keys(detailsDictionary).map(endpoint => (
            <div key={endpoint}>
              <h3>{endpoint} Mandate Details</h3>
              <ul>
                {detailsDictionary[endpoint].map((detail, index) => (
                  <li key={`${endpoint}-${index}`}>
                    Trader Mandate ID: {detail.traderMandateId}, Trader Name: {detail.traderName}
                  </li>
                ))}
              </ul>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default MandateAccessButton;
