import React, { useState } from 'react';

const App = () => {
  const [detailsFetched, setDetailsFetched] = useState(false);
  const endpoints = [0, 1];
  const [endpointDetails, setEndpointDetails] = useState({
    0: { tradermandateID: null, traderNAME: null },
    1: { tradermandateID: null, traderNAME: null },
  });

  const handleFetchDetails = async () => {
    if (!detailsFetched) {
      try {
        const promises = endpoints.map(async (endpoint) => {
          const response = await fetch(`http://localhost:3010/${endpoint}`);
          const data = await response.json();
          return {
            tradermandateID: data.tradermandateID,
            traderNAME: data.traderNAME,
          };
        });
        const results = await Promise.all(promises);
        const mergedDetails = results.reduce((acc, result, index) => {
          const endpoint = endpoints[index];
          return { ...acc, [endpoint]: result };
        }, {});
        setEndpointDetails(mergedDetails);
        setDetailsFetched(true);
      } catch (error) {
        console.error('Error fetching endpoint details:', error);
      }
    }
  };

  return (
    <div>
      <MandateAccessButton onClick={handleFetchDetails} disabled={detailsFetched} />
      {detailsFetched && (
        <div>
          <EndpointDetails details={endpointDetails[0]} />
          <EndpointDetails details={endpointDetails[1]} />
        </div>
      )}
    </div>
  );
};

const MandateAccessButton = ({ onClick, disabled }) => (
  <button onClick={onClick} disabled={disabled}>
    Fetch Endpoint Details
  </button>
);

const EndpointDetails = ({ details }) => (
  <div>
    {details ? (
      <div>
        <h2>Endpoint Details</h2>
        <p>Trader Mandate ID: {details.tradermandateID}</p>
        <p>Trader Name: {details.traderNAME}</p>
      </div>
    ) : (
      <p>Loading...</p>
    )}
  </div>
);

export default App;
