import React, { useState } from 'react';

const MandateAccessButton = ({ endpoints }) => {
  const [showDetails, setShowDetails] = useState(false);
  const [mandateDetails, setMandateDetails] = useState([]);

  const handleButtonClick = async () => {
    try {
      const responsePromises = endpoints.map(endpoint =>
        fetch(`http://localhost:3010/${endpoint}`)
          .then(response => {
            if (!response.ok) {
              throw new Error(`Failed to fetch data from ${response.url}. Status: ${response.status}`);
            }
            return response.json();
          })
      );

      const dataArray = await Promise.all(responsePromises);

      const combinedDetails = dataArray.flatMap(data => {
        // Ensure data is an array before mapping over it
        if (Array.isArray(data)) {
          return data.map(item => ({
            traderMandateId: item.traderMandateId,
            traderName: item.traderName
            // Add more fields as needed
          }));
        } else {
          // Handle unexpected data format if necessary
          return [];
        }
      });

      setShowDetails(true);
      setMandateDetails(combinedDetails);
    } catch (error) {
      console.error('Error fetching data:', error.message);
      // Handle error state or notification here if needed
    }
  };

  return (
    <div>
      <button onClick={handleButtonClick}>Generate Mandate Access</button>
      {showDetails && (
        <div>
          <h2>Trader Mandate Details</h2>
          <ul>
            {mandateDetails.map((detail, index) => (
              <li key={index}>
                Trader Mandate ID: {detail.traderMandateId}, Trader Name: {detail.traderName}
                {/* Render additional fields here */}
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
};

export default MandateAccessButton;
